Method.Source:
public static MXTRefreshMetadataAssemblyCacheController construct()
{
    MXTRefreshMetadataAssemblyCacheController controller;

    controller  = new MXTRefreshMetadataAssemblyCacheController(
    classStr(MXTRefreshMetadataAssemblyCache),
    methodStr(MXTRefreshMetadataAssemblyCache, run),
    SysOperationExecutionMode::ScheduledBatch);


    return controller;
}

public static void main(Args _args)
{
    MXTRefreshMetadataAssemblyCacheController   controller  = MXTRefreshMetadataAssemblyCacheController::construct();
    MXTRefreshMetadataAssemblyCacheContract contract = controller.getDataContractObject() as MXTRefreshMetadataAssemblyCacheContract;

    controller.parmLoadFromSysLastValue(false);

    if (_args.dataset() == tableNum(MXTXppMetadataAssemblyCache))
    {
        MXTXppMetadataAssemblyCache cache = _args.record();

        contract.parmReferenceName(cache.ReferenceName);
    }

    controller.parmDialogCaption("@MXT:RefreshAssemblyMetadataCache");
    controller.startOperation();
}
