Method.Source:
[SubscribesTo(classStr(ApplicationStartupEventManager), staticDelegateStr(ApplicationStartupEventManager, onInteractiveSessionCreated))]
public static void ApplicationStartupEventManager_onInteractiveSessionCreated()
{
    MXTXppEventHandlerTable handlerTable;

    while select pessimisticlock handlerTable
        where handlerTable.Active
    {
        try
        {
            MXTXppEventHandlerManager manager = new MXTXppEventHandlerManager(handlerTable);
            manager.activate();
        }
        catch
        {
            ttsbegin;
            handlerTable.selectForUpdate(true);
            handlerTable.setInactive();
            handlerTable.doUpdate();
            ttscommit;
        }
    }
}
