public static MXTXppInterpreterScript find(RecId _scriptId, boolean _forUpdate = false)
{
    MXTXppInterpreterScript script;

    select firstonly script
        where script.RecId == _scriptId;

    if (_forUpdate)
    {
        script.selectForUpdate(true);
    }

    return script;
}

/// <summary>
///
/// </summary>
public void delete()
{
    MXTXppInterpreterScriptDependency dependency;

    ttsbegin;

    delete_from dependency
        where dependency.ScriptId == this.RecId;

    super();

    ttscommit;
}

/// <summary>
///
/// </summary>
/// <returns></returns>
public boolean validateDelete()
{
    boolean ret;

    ret = super();

    if (ret && this.RecId)
    {
        // Check if it a dependency exist
        if (MXTXppInterpreterScriptDependency::isDependency(this.RecId))
        {
            return checkFailed("Script cannot be deleted as it is included as a dependency of other scripts. Delete dependant first.");
        }
    }

    return ret;
}

    /// <summary>
    /// Creates a new service script
    /// </summary>
    public static MXTXppInterpreterScript createService(MXTServiceId _serviceId, Description _description)
    {
        MXTXppInterpreterScript serviceScript;

        serviceScript.Type = MXTXppInterpreterScriptType::Service;
        serviceScript.Description = _description;
        serviceScript.SourceCode = strFmt(@'/// <summary>
/// Service entry point.
/// Request parameters are mapped to method parameters,
/// and the return value is sent back to the caller.
/// </summary>
str %1(str _parm1, int _parm2)
{
    // TODO: Implement logic
    return "";
}', _serviceId);

        serviceScript.insert();

        return serviceScript;
    }

public boolean validateWrite()
{
    boolean ret = super();;

    if (this.Type == MXTXppInterpreterScriptType::Service)
    {
        ret = ret && MXTXppInterpreterService::validateServiceCode(this.SourceCode);
    }

    return ret;
}
