SourceCode.Declaration:
using Parser = XppInterpreter.Parser;
using Lexer = XppInterpreter.Lexer;

public class MXTXppInterpreterService extends common
{
}

Method.Source:
public static MXTXppInterpreterService find(MXTServiceId _serviceId, boolean _forUpdate = false)
{
    MXTXppInterpreterService service;

    if (_serviceId)
    {
        select firstonly service
            index ServiceIdx
            where service.ServiceId == _serviceId;

        if (_forUpdate)
        {
            service.selectForUpdate(true);
        }
    }

    return service;
}

public static MXTXppInterpreterService findByScript(RefRecId _scriptRecId, boolean _forUpdate = false)
{
    MXTXppInterpreterService service;

    if (_scriptRecId)
    {
        select firstonly service
            where service.Script == _scriptRecId;

        if (_forUpdate)
        {
            service.selectForUpdate(true);
        }
    }
    return service;
}

public static boolean validateServiceCode(str _sourceCode)
{
    Parser.FunctionDeclaration entryPoint;
    Parser.ParseResult result = MXTXppInterpreterExecutor::parse(_sourceCode);

    if (result.HasErrors)
    {
        return checkFailed("Service script cannot be saved because It contains errors.");
    }

    Parser.Program program = result.AST;

    // Get statements
    System.Collections.IEnumerable statements = program.Statements;
    System.Collections.IEnumerator enumerator = statements.GetEnumerator();

    int statementCount;

    while (enumerator.MoveNext())
    {
        statementCount ++;

        if (statementCount > 1)
        {
            return checkFailed("Service scripts must consist of a single function.");
        }

        entryPoint = enumerator.Current;
    }

    if (!entryPoint)
    {
        return checkFailed("No entry point found in service script.");
    }

    if (entryPoint.Type.TokenType == Lexer.TType::Void)
    {
        return checkFailed("Service entry point must return a value.");
    }

    return true;
}
