/// <summary>
/// Updates the service parameters based on the current script source code.
/// </summary>
public static void updateServiceParameters(MXTXppInterpreterService _service)
{
    MXTXppInterpreterScript script = _service.findScript();

    XppInterpreter.Parser.FunctionDeclaration entryPoint = MXTXppInterpreterServiceRequestDeserializer::getEntryPointFromSourceCode(script.SourceCode);

    anytype parmDictionary = XppInterpreter.Interpreter.XppInterpeterUtil::GetFunctionParameters(entryPoint);
    System.Collections.IEnumerator parmEnumerator = parmDictionary.GetEnumerator();

    Set foundParams = new Set(Types::String);

    while (parmEnumerator.MoveNext())
    {
        System.Collections.Generic.KeyValuePair<System.String, System.String> pair = parmEnumerator.Current;

        str parameterName = pair.Key;
        str parameterTypeStr = pair.Value;

        Types parameterType = MXTXppCastingProxy::getXppTypeByTypeName(parameterTypeStr);

        MXTXppInterpreterServiceParameters parameter = MXTXppInterpreterServiceParameters::find(_service.ServiceId, parameterName, true);

        ttsbegin;

        if (parameter)
        {
            // Update existing parameter
            parameter.Type = parameterType;
            parameter.update();
        }
        else
        {
            // Create new parameter
            parameter.ServiceId = _service.ServiceId;
            parameter.ParameterName = parameterName;
            parameter.Type = parameterType;
            parameter.insert();
        }

        ttscommit;

        foundParams.add(parameterName);
    }

    // Delete parameters that are no longer present
    MXTXppInterpreterServiceParameters parameterToDelete;
    while select forupdate parameterToDelete
        where parameterToDelete.ServiceId == _service.ServiceId
    {
        if (!foundParams.in(parameterToDelete.ParameterName))
        {
            ttsbegin;
            parameterToDelete.delete();
            ttscommit;
        }
    }
}

public static MXTXppInterpreterServiceParameters find(MXTServiceId _serviceId, Name _parameterName, boolean _forUpdate = false)
{
    MXTXppInterpreterServiceParameters parameter;

    if (_serviceId && _parameterName)
    {

        select parameter
            where parameter.ServiceId      == _serviceId
               && parameter.ParameterName  == _parameterName;

        if (_forUpdate)
        {
            parameter.selectForUpdate(true);
        }
    }

    return parameter;
}
